import React from "react";
import ReactDom from "react-dom/client"
const pizzaData = [
    {
      name: "Focaccia",
      ingredients: "Bread with italian olive oil and rosemary",
      price: 6,
      photoName: "pizzas/focaccia.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Margherita",
      ingredients: "Tomato and mozarella",
      price: 10,
      photoName: "pizzas/margherita.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Spinaci",
      ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
      price: 12,
      photoName: "pizzas/spinaci.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Funghi",
      ingredients: "Tomato, mozarella, mushrooms, and onion",
      price: 12,
      photoName: "pizzas/funghi.jpg",
      soldOut: false,
    },
    {
      name: "Pizza Salamino",
      ingredients: "Tomato, mozarella, and pepperoni",
      price: 15,
      photoName: "pizzas/salamino.jpg",
      soldOut: true,
    },
    {
      name: "Pizza Prosciutto",
      ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
      price: 18,
      photoName: "pizzas/prosciutto.jpg",
      soldOut: false,
    },
  ];
  
function App(){
    const x = "dom maipulation"
    return (<div>
        <Header/>
        <Menu/>
        <Footer/>
          </div>
);}

function Header() {
    return <h1 style={{color: "red", fontSize: "48px", }}> The Big Pizza Company</h1>
}

function Menu() {
    return(
        <>
        <h2>Our Menu</h2>
        <Pizza />
        <Pizza />
        </>
    )
}

function Footer() {
    const hour = new Date().getHours()
    const openHour = 12;
    const closeHour = 22;
    const isOpen = hour >= openHour && hour <= closeHour;
    console.log(isOpen)
    // if(hour >= openHour && hour <= closeHour) alert ("we're currently open!");
    // else alert ("sorry we're closed");
    // console.log(hour)
    // We're currently open
    return <footer>{new Date().toLocaleTimeString()} {
        (hour >= openHour && hour <= closeHour) ? "we are open":"WE ARE CLOSED" 
    }</footer>
}
function Pizza(){
    return (
        <div>
    <img src="pizzas\salamino.jpg"/>
    <h2>Pizza Salamino</h2> 
    <p>Tomato, mozarella, ham, aragula, and burrata cheese</p>
    </div>
    );
}



const root = ReactDom.createRoot(document.getElementById("root"))
root.render(
<React.StrictMode>
    <App/>
</React.StrictMode>
);
